```javascript
function weeklyMaintenance() {
  const tracker = SpreadsheetApp.getActiveSpreadsheet().getSheetByName('Tracker');
  const summary = SpreadsheetApp.getActiveSpreadsheet().getSheetByName('Summary');
  const today = new Date();
  const day = today.getDay(); // Sunday = 0

  // --- run only on Sunday ---
  if (day !== 0) return;

  // 1. Clear prior week’s checkboxes
  tracker.getRange('B2:D8').clearContent();

  // 2. Collect last week’s averages and append them to Summary
  const completion = tracker.getRange('G9').getValue();
  const mood = tracker.getRange('E9').getValue();
  const lastRow = summary.getLastRow() + 1;
  summary.getRange(lastRow, 1, 1, 3)
         .setValues([[lastRow - 1, completion, mood]]);

  // 3. Timestamp
  tracker.getRange('A1').setValue('Last reset: ' + today);

  // 4. Optional backup
  const ss = SpreadsheetApp.getActiveSpreadsheet();
  const file = DriveApp.getFileById(ss.getId());
  const backupName = 'SelfCareBackup_' + today.toISOString().slice(0,10);
  const backupFolder = DriveApp.getFolderById('YOUR_BACKUP_FOLDER_ID'); // replace with your folder ID
  file.makeCopy(backupName, backupFolder);
}
```

