\usepackage{fancyhdr}
\pagestyle{fancy}
\fancyhf{} % clear defaults

% Header
\fancyhead[L]{\textit{Peer‑Support Quick Reference}}
\fancyhead[R]{\textit{For non‑clinical emotional assistance}}

