    light.disabled = true;
    btn.textContent = '☀️ Switch Theme';
    localStorage.setItem('themePref', 'dark');
  }
});

// Auto‑apply saved choice on load
window.addEventListener('DOMContentLoaded', () => {
  const pref = localStorage.getItem('themePref');
  if (pref === 'dark') {
    document.getElementById('darkTheme').disabled = false;
    document.getElementById('lightTheme').disabled = true;
    btn.textContent = '☀️ Switch Theme';
  }
});
```

